package org.reactnative.camera.events;

import android.support.v4.util.Pools;
import android.util.SparseArray;

import org.reactnative.camera.CameraViewManager;
import org.reactnative.camera.utils.ImageDimensions;
import org.iopan.motiondetector.MotionDetectorUtils;
import com.facebook.react.bridge.Arguments;
import com.facebook.react.bridge.WritableArray;
import com.facebook.react.bridge.WritableMap;
import com.facebook.react.uimanager.events.Event;
import com.facebook.react.uimanager.events.RCTEventEmitter;
import com.google.android.cameraview.CameraView;
// import com.google.android.gms.vision.face.Face;
import com.facebook.react.bridge.WritableMap;

public class MotionDetectedEvent extends Event<MotionDetectedEvent> {
  private static final Pools.SynchronizedPool<MotionDetectedEvent> EVENTS_POOL =
      new Pools.SynchronizedPool<>(3);

  private double mScaleX;
  private double mScaleY;
  private SparseArray<WritableMap> mMotion;
  private ImageDimensions mImageDimensions;

  private MotionDetectedEvent() {}

  public static MotionDetectedEvent obtain(
      int viewTag,
      SparseArray<WritableMap> motion,
      ImageDimensions dimensions,
      double scaleX,
      double scaleY
  ) {
    MotionDetectedEvent event = EVENTS_POOL.acquire();
    if (event == null) {
      event = new MotionDetectedEvent();
    }
    event.init(viewTag, motion, dimensions, scaleX, scaleY);
    return event;
  }

  private void init(
      int viewTag,
      SparseArray<WritableMap> motion,
      ImageDimensions dimensions,
      double scaleX,
      double scaleY
  ) {
    super.init(viewTag);
    mMotion = motion;
    mImageDimensions = dimensions;
    mScaleX = scaleX;
    mScaleY = scaleY;
  }

  /**
   * note(@sjchmiela)
   * Should the events about detected motion coalesce, the best strategy will be
   * to ensure that events with different motion count are always being transmitted.
   */
  @Override
  public short getCoalescingKey() {
    if (mMotion.size() > Short.MAX_VALUE) {
      return Short.MAX_VALUE;
    }

    return (short) mMotion.size();
  }

  @Override
  public String getEventName() {
    return CameraViewManager.Events.EVENT_ON_MOTION_DETECTED.toString();
  }

  @Override
  public void dispatch(RCTEventEmitter rctEventEmitter) {
    rctEventEmitter.receiveEvent(getViewTag(), getEventName(), serializeEventData());
  }

  private WritableMap serializeEventData() {
    WritableArray motionList = Arguments.createArray();

    for(int i = 0; i < mMotion.size(); i++) {
      WritableMap motion = mMotion.valueAt(i);

      WritableMap serializedFace = Arguments.createMap();
      serializedFace.putString("color",motion.getString("color"));
      serializedFace.putInt("x",motion.getInt("x"));
      serializedFace.putInt("y",motion.getInt("y"));

      motionList.pushMap(serializedFace);
    }

    WritableMap event = Arguments.createMap();
    event.putString("type", "motion");
    event.putArray("motion", motionList);
    event.putInt("target", getViewTag());
    return event;
  }
}
